import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Observable } from 'rxjs';
import { Feedback } from 'src/app/models/feedback.model';
import { RequestsService } from 'src/app/services/requests.service';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.scss']
})
export class FormComponent implements OnInit {

  messageList$!: Observable<any[]>;
  topicList$!: Observable<any[]>;
  topicList: any = [];

  TopicMap:Map<string, string> = new Map();

  feedback: Feedback = {
    name: '',
    email: '',
  };

  constructor(private requestsServise: RequestsService, private router: Router){}

  ngOnInit(): void {
    this.messageList$ = this.requestsServise.getAllMessages();
    this.topicList$ = this.requestsServise.getAllTopics();
    this.refreshTopicMap();
    console.log(this.TopicMap);
  }

  refreshTopicMap(){
    this.requestsServise.getAllTopics().subscribe(data => {
      this.topicList = data;

      for( let i of data){
        this.TopicMap.set(this.topicList[i].id, this.topicList[i].topicName)
      }
    })


  }

  addRequest() {
    this.requestsServise.addMessage(this.feedback)
    .subscribe({
      next: (feedback) =>{
        this.router.navigate(['request/:id']);
      }
    });
  }

}

<div class="feedback">
  <form class="feedback__form form" #form="ngForm"  (ngSubmit)="addRequest()">
    <div class="form__title">Напишите нам</div>
    <div class="form__questionnaire questionnaire">
       <div for="name" class="questionnaire__question">Ваше имя:</div>
      <input type="text" class="questionnaire__text" id="name" name ="name" [(ngModel)] = "addFormRequest.name">

      <div for="email" class="questionnaire__question">Ваш Email:</div>
      <input type="email" class="questionnaire__text" id="email" name ="email" [(ngModel)] = "addFormRequest.email">

      <div for="phone" class="questionnaire__question">Ваш телефон:</div>
      <input type="text" class="questionnaire__text" id="phone" name ="phone" [(ngModel)] = "addFormRequest.phone">
      <div for="topic" class="questionnaire__question">Тема:</div>
      <select name="" id="topic" class="questionnaire__text" name ="topic" [(ngModel)] = "addFormRequest.topic">
        <option value="1">Техподдержка</option>
        <option value="1">Продажи</option>
        <option value="1">Другое</option>
      </select>

      <div for="message" class="questionnaire__question">Ваше сообщение:</div>
      <textarea name="message" id="message" cols="30" rows="4" class="questionnaire__text" name ="message" [(ngModel)] = "addFormRequest.message"></textarea> 

    </div>

    <button type="submit" class="form__button">Отправить письмо!</button>
  </form>

</div>
