import { Component, OnInit } from '@angular/core';
import { Feedback } from 'src/app/models/feedback.model'
import { RequestsService } from 'src/app/services/requests.service';

@Component({
  selector: 'app-request',
  templateUrl: './request.component.html',
  styleUrls: ['./request.component.scss']
})
export class RequestComponent implements OnInit{

  requests: Feedback[] = [];

  constructor(private requestsServise: RequestsService) {}

  ngOnInit(): void{
    this.requestsServise.getAllRequests()
    .subscribe({
      next: (requests) => {
        this.requests = requests;
      },
      error: (response) => {
        console.log(response);

      }
    })
  }
}

<div class="feedback">
  <div class="feedback__window window">
    <div class="window__title">Сообщение отправлено</div>
    <div class="window__questionnaire questionnaire">
      <div class="questionnaire__question">Ваше имя:</div>
      <div class="questionnaire__text">{{messageList$[0].contacts.contactName}}</div>

      <div class="questionnaire__question">Ваш Email:</div>
      <div class="questionnaire__text">{{messageList$[0].contacts.contactEmail}}</div>

      <div class="questionnaire__question">Ваш телефон:</div>
      <div class="questionnaire__text">{{messageList$[0].contacts.contactPhone}}</div>

      <div class="questionnaire__question">Тема:</div>
      <div class="questionnaire__text">{{messageList$[0].topics.topicName}}</div>

      <div class="questionnaire__question">Ваше сообщение:</div>
      <div class="questionnaire__text">{{messageList$[0].messageText}}</div>

    </div>
  </div>
</div>
