import { Component, OnInit } from '@angular/core';
import { Feedback } from 'src/app/models/feedback.model'

@Component({
  selector: 'app-request',
  templateUrl: './request.component.html',
  styleUrls: ['./request.component.scss']
})
export class RequestComponent implements OnInit{

  requests: Feedback[] = [
    {
      id: '5b4ed4cc',
      name: 'Lisa',
      email: 'lisa@mail.com',
      phone: 89000000001,
      topic: '1',
      message: 'Lorem ipsum dolor sit amet consectetur, adipisicing elit. Nam nihil porro praesentium.'
    },
    {
      id: '5b4ed4c1',
      name: 'Lesa',
      email: 'lesa@mail.com',
      phone: 89000000002,
      topic: '2',
      message: 'Lorem ipsum dolor sit amet consectetur, adipisicing elit. Nam nihil.'
    },
  ];

  constructor() {}

  ngOnInit(): void{

    // this.requests.push()
  }
}

<div class="feedback">
  <div class="feedback__window window">
    <div class="window__title">Ваше сообщение отправлено</div>
    <div class="window__questionnaire questionnaire">
      <div class="questionnaire__question">Ваше имя:</div>
      <div class="questionnaire__text">{{requests[0].name}}</div>

      <div class="questionnaire__question">Ваш Email:</div>
      <div class="questionnaire__text">{{requests[0].email}}</div>

      <div class="questionnaire__question">Ваш телефон:</div>
      <div class="questionnaire__text">{{requests[0].phone}}</div>

      <div class="questionnaire__question">Тема:</div>
      <div class="questionnaire__text">{{requests[0].topic}}</div>

      <div class="questionnaire__question">Ваше сообщение:</div>
      <div class="questionnaire__text"></div>

    </div>
  </div>
</div>
