import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { RequestsService } from 'src/app/services/requests.service';

@Component({
  selector: 'app-request',
  templateUrl: './request.component.html',
  styleUrls: ['./request.component.scss']
})
export class RequestComponent implements OnInit{


  constructor(private requestsServise: RequestsService) {}



  messageList$!: Observable<any[]>;
  topicList$!: Observable<any[]>;
  messageList: any = [];
  topicList: any = [];

  TopicMap:Map<string, string> = new Map();

    ngOnInit(): void {
    this.messageList$ = this.requestsServise.getAllMessages();
    this.topicList$ = this.requestsServise.getAllTopics();
    this.messageOutput();
    this.refreshTopicMap();    

  }

  messageOutput(){
    this.requestsServise.getAllMessages().subscribe(data => {
      this.messageList = data;
    });
  }

  refreshTopicMap(){
    this.requestsServise.getAllTopics().subscribe(data => {
      this.topicList = data;

      for( let i in data){
        this.TopicMap.set(this.topicList[i].id, this.topicList[i].topicName)
      }    
      console.log(this.topicList[0].id);
    });


  }



