import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { Observable } from 'rxjs';
import { Contact } from 'src/app/models/contact.model';
import { Message } from 'src/app/models/message.model';
import { RequestsService } from 'src/app/services/requests.service';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.scss']
})
export class FormComponent implements OnInit {

  contactList$!: Observable<any[]>;
  topicList$!: Observable<any[]>;
  contactList: any = [];
  topicList: any = [];
  newContact: boolean = true;
  siteKey: string = '6LdFQr4lAAAAAPAR37RURyid0rOym5E0N7RQdoUO';

  TopicMap:Map<string, string> = new Map();

  contact: Contact = {
    id: 0,
    ContactName: '',
    ContactEmail: '',
    ContactPhone: '',
  };

  message: Message = {
    id: 0,
    TopicId: 0,
    ContactId: 0,
    MessageText: '',
  };

  formProv = new FormGroup({
    name: new FormControl('',Validators.required),
    email: new FormControl('',[Validators.required, Validators.email]),
    phone: new FormControl('',[Validators.required, Validators.pattern('[0-9]{11,15}')]),
    topic: new FormControl('',Validators.required),
    mes:new FormControl('',Validators.required),
    captcha: new FormControl('',Validators.required)
  })

  constructor(private requestsServise: RequestsService, private router: Router, private formBuilder: FormBuilder){}

  ngOnInit(): void {
    this.contactList$ = this.requestsServise.getAllContacts();
    this.topicList$ = this.requestsServise.getAllTopics();
    this.refreshTopicMap();
  }



  refreshTopicMap(){
    this.requestsServise.getAllTopics().subscribe(data => {
      this.topicList = data;

      for( let i of data){
        this.TopicMap.set(this.topicList[i].id, this.topicList[i].topicName)
      }
    })

  }

  get name(){
    return this.formProv.get('name');
  }
  get email(){
    return this.formProv.get('email');
  }
  get phone(){
    return this.formProv.get('phone');
  }
  get topic(){
    return this.formProv.get('topic');
  }
  get mes(){
    return this.formProv.get('mes');
  }
  get captcha(){
    return this.formProv.get('captcha');
  }

  addRequest() {
    this.requestsServise.getAllContacts().subscribe(data => {
      this.contactList = data;
      this.newContact = true;
      for (let i in this.contactList){

        if ((this.contactList[i].contactEmail == this.contact.ContactEmail) && (this.contactList[i].contactPhone == this.contact.ContactPhone)){
          this.newContact = false; 
          this.message.ContactId = data[+i].id;          
        }
      }

    });
    this.requestsServise.addMessage(this.message).subscribe({
      next: (result) => {
        this.router.navigate(['request/:id']);
      }
    });


    if (this.newContact == true){
      this.requestsServise.addContact(this.contact).subscribe({
        next: (result) => {
          this.message.ContactId = Object.values(result)[0];
          this.requestsServise.addMessage(this.message).subscribe({
            next: (result) => {
              this.router.navigate(['request/:id']);
            }
          });
        }
      });


    } 



  }

}

<div class="feedback">
  <form class="feedback__form form" #form="ngForm"  (ngSubmit)="addRequest()" [formGroup]="formProv">
    <div class="form__title">Напишите нам</div>
    <div class="form__questionnaire questionnaire">
       <div for="name" class="questionnaire__question">Ваше имя:</div>
      <input type="text" class="questionnaire__text" id="name" name ="name" [(ngModel)] = "contact.ContactName" formControlName = 'name' [ngClass]="{validation:(name&&name.invalid&&name.touched)}">

      <div for="email" class="questionnaire__question">Ваш Email:</div>
      <input type="email" class="questionnaire__text" id="email" name ="email" [(ngModel)] = "contact.ContactEmail" formControlName = 'email' [ngClass]="{validation:(email&&email.invalid&&email.touched)}">

      <div for="phone" class="questionnaire__question">Ваш телефон:</div>
      <input type="text" class="questionnaire__text" id="phone" name ="phone" [(ngModel)] = "contact.ContactPhone" formControlName = 'phone' [ngClass]="{validation:(phone&&phone.invalid&&phone.touched)}">

      <div class="questionnaire__question">Тема:</div>
      <select name="topicId" id="topicId" class="questionnaire__text" [(ngModel)] = "message.TopicId" formControlName = 'topic' [ngClass]="{validation:topic && topic.touched && !topic.dirty}">
        <option *ngFor="let topic of topicList$|async" [value]="topic.id">{{topic.topicName}}</option>
      </select>

      <div for="message" class="questionnaire__question">Ваше сообщение:</div>
      <textarea name="message" id="message" cols="30" rows="4" class="questionnaire__text" name ="message" [(ngModel)] = "message.MessageText" formControlName = 'mes' [ngClass]="{validation:(mes&&mes.invalid&&mes.touched)}"></textarea> 
      <ngx-recaptcha2 class="questionnaire__captcha" #captchaElem [siteKey]="siteKey"></ngx-recaptcha2>
    </div>


    <button type="submit" class="form__button" [disabled]="formProv.invalid">Отправить письмо!</button>
  </form>

</div>
